mapper.select:
SELECT FACILITY_RESERVE_ID
FROM FACILITY_RESERVE_DETAIL_TEST
WHERE 1=1
  AND FACILITY_RESERVE_NM = #{facilityNm}
  AND FACILITY_RESERVE_DT = #{facilityReserveDt}
  AND RESERVE_TIME_INTERVAL BETWEEN #{startInterval} AND #{endInterval}

mapper.insert:
INSERT
INTO FACILITY_RESERVE_TEST(
                            FACILITY_RESERVE_ID
                          , FACILITY_REMARKS
                          , ANIMAL_PK
                          , MEMBER_PK )
VALUES(
        #{facilityReserveId}
      , #{facilityRemarks}
      , #{animalId}
      , #{memberPk}
      )

INSERT
INTO FACILITY_RESERVE_DETAIL_TEST( FACILITY_RESERVE_ID
                                 , FACILITY_RESERVE_DT
                                 , FACILITY_NM
                                 , RESERVE_TIME_INTERVAL )
VALUES(
        #{facilityReserveId}
      , #{facilityReserveDt}
      , #{facilityNm}
      , #{timeInterval}
      )

mapper.update:
UPDATE FACILITY_RESERVE_TEST
SET FACILITY_REMARKS = #{facilityRemarks}
  , ANIMAL_PK = #{animalId}
WHERE FACILITY_RESERVE_ID =#{facilityReserveId}
