mapper.select:
SELECT FACILITY_RESERVE_ID , FACILITY_NM , USER_NM , ANIMAL_NM , FACILITY_RESERVE_DT , TO_CHAR(START_TIME + 30/(24*60)*RESERVE_START_TIME , 'hh24:mi') AS START_TIME , TO_CHAR(START_TIME + 30/(24*60)*(RESERVE_END_TIME+1), 'hh24:mi') AS END_TIME FROM (SELECT R.FACILITY_RESERVE_ID , MAX(FACILITY_NM) AS FACILITY_NM , MAX(MEMBER_MEMBERNAME) AS USER_NM , MAX(ANIMAL_NM) AS ANIMAL_NM , MAX(FACILITY_RESERVE_DT) AS FACILITY_RESERVE_DT , MIN(RESERVE_TIME_INTERVAL) AS RESERVE_START_TIME , MAX(RESERVE_TIME_INTERVAL) AS RESERVE_END_TIME , TO_DATE('2019-08-21 10:00', 'yyyy-mm-dd hh24:mi') AS START_TIME FROM FACILITY_RESERVE_TEST R INNER JOIN USER_TEST5 U ON 1=1 AND R.MEMBER_PK = U.MEMBER_PK INNER JOIN FACILITY_RESERVE_DETAIL_TEST D ON 1=1 AND R.FACILITY_RESERVE_ID = D.FACILITY_RESERVE_ID INNER JOIN ANIMAL_TEST A ON 1=1 AND R.ANIMAL_PK = A.ANIMAL_PK WHERE 1=1 AND D.FACILITY_RESERVE_DT >= TO_CHAR(SYSDATE, 'YYYY-MM-DD') GROUP BY R.FACILITY_RESERVE_ID)
AND MEMBER_PK = #{memberPk}
ORDER BY FACILITY_RESERVE_DT ASC , START_TIME ASC

SELECT FACILITY_RESERVE_ID
FROM FACILITY_RESERVE_DETAIL_TEST
WHERE 1=1
  AND FACILITY_NM = #{facilityNm}
  AND FACILITY_RESERVE_DT = #{facilityReserveDt}
  AND RESERVE_TIME_INTERVAL BETWEEN #{startTimeInterval} AND #{endTimeInterval}

SELECT FACILITY_RESERVE_ID
FROM FACILITY_RESERVE_DETAIL_TEST
WHERE 1=1
  AND FACILITY_NM = #{facilityNm}
  AND FACILITY_RESERVE_DT = #{facilityReserveDt}
  AND RESERVE_TIME_INTERVAL BETWEEN #{startTimeInterval} AND #{endTimeInterval}
  AND FACILITY_RESERVE_ID != #{facilityReserveId}

mapper.insert:
INSERT
INTO FACILITY_RESERVE_TEST(
                            FACILITY_RESERVE_ID
                          , FACILITY_REMARKS
                          , ANIMAL_PK
                          , MEMBER_PK )
VALUES(
        #{facilityReserveId}
      , #{facilityRemarks}
      , #{animalId}
      , #{memberPk}
      )

INSERT
INTO FACILITY_RESERVE_DETAIL_TEST( FACILITY_RESERVE_ID
                                 , FACILITY_RESERVE_DT
                                 , FACILITY_NM
                                 , RESERVE_TIME_INTERVAL )
VALUES(
        #{facilityReserveId}
      , #{facilityReserveDt}
      , #{facilityNm}
      , #{timeInterval}
      )

mapper.update:
UPDATE FACILITY_RESERVE_TEST
SET FACILITY_REMARKS = #{facilityRemarks}
  , ANIMAL_PK = #{animalId}
WHERE FACILITY_RESERVE_ID =#{facilityReserveId}
