mapper.select:
SELECT a.notice_pk AS notice_pk, a.notice_title AS notice_title, a.notice_content AS notice_content, a.notice_hits AS notice_hits, a.notice_time AS notice_time, a.notice_highlighted AS notice_highlighted, a.member_pk AS member_pk, b.member_membername AS member_membername FROM notice_test2 a INNER JOIN user_test5 b ON a.member_pk = b.member_pk
WHERE a.notice_pk = #{NOTICE_PK}
ORDER BY a.notice_pk ASC

mapper.update:
UPDATE notice_test2
SET NOTICE_HITS = NOTICE_HITS + 1
WHERE NOTICE_PK = #{NOTICE_PK}

UPDATE notice_test2
SET notice_title = #{NOTICE_TITLE},
    notice_content = #{NOTICE_CONTENT}
WHERE notice_pk = #{NOTICE_PK}
